import { Component, Input } from '@angular/core';
import { Setting } from 'src/app/configurations/internship-settings/setting';

@Component({
  selector: 'app-internship-option-card',
  templateUrl: './internship-option-card.component.html',
  styleUrls: ['./internship-option-card.component.scss']
})
export class InternshipOptionCardComponent {
  @Input() setting!: Setting
}
<div class="option-body">
    <h6 class="card-setting-name">
        {{ setting.name }}
        <span class="arrow">
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" class="long-arrow">
                <path d="M0 0h24v24H0z" fill="none"/>
                <path d="M14 7l-1.41 1.41L16.17 12l-3.58 3.59L14 17l6-6z"/>
              </svg>
        </span>
    </h6>
    <p class="card-setting-description">{{ setting.description }}</p>
</div>
