import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { SignupRequest } from '../core/interfaces/signup-request';
import { AuthenticationService } from '../core/services/authentication.service';
import { AdminRole } from '../core/interfaces/admin-role';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.scss']
})
export class SignupComponent implements OnInit {

  private adminRole: AdminRole | undefined;

  public constructor(private formBuilder: FormBuilder,
    private authService: AuthenticationService,
    private router: Router) {}


    public ngOnInit(): void {
      this.authService.getAdminRole()
      .subscribe((response: AdminRole) => {
        this.adminRole = response;
        console.log(response)
      })
  }

    public registrationForm = this.formBuilder.group({
      firstName: ['', [Validators.required]],
      lastName: ['', [Validators.required]],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required]],
    })

    public getSignupValuesFromForm = () : SignupRequest => {
      const roles: string[] = [this.adminRole!.id];
      const signupRequest: SignupRequest = {
        firstName: this.registrationForm.value.firstName!,
        lastName: this.registrationForm.value.lastName!,
        email: this.registrationForm.value.email!,
        password: this.registrationForm.value.password!,
        roles: roles
      }

      return signupRequest;
    }

    public submitSignupForm = () => {
      const request: SignupRequest = this.getSignupValuesFromForm();
      this.authService.submitSignUpRequest(request)
      .subscribe({
        next: response => {
          // Handle successful response 
          // Should redirect to login page
          console.log('Registration successful:', response);
          this.router.navigate(['/auth/signin']);
        },
        error: error => {
          // Handle error
          // Api will return an error object
          console.error('Registration failed:', error);
        }
      });
    }
}
<div class="row w-100 form-title">
    <h1>Create your account</h1>

        <form [formGroup]='registrationForm' (ngSubmit)="submitSignupForm()" class="row form-body">

            <div class="row form-field-row">
                <label for="">First Name</label>
                <input class="form-input" type="text" formControlName="firstName">
                <p class="error-message" *ngIf="registrationForm.controls.firstName.touched && registrationForm.controls.firstName.errors?.['required']">
                    First name is required.
                </p>
            </div>

            <div class="row form-field-row">
                <label for="">Last Name</label>
                <input type="text" formControlName="lastName">
                <p class="error-message" *ngIf="registrationForm.controls.lastName.touched && registrationForm.controls.lastName.errors?.['required']">
                    Last name is required.
                </p>
            </div>

            <div class="row form-field-row">
                <label for="">Email</label>
                <input type="text" formControlName="email">
                <p class="error-message" *ngIf="registrationForm.controls.email.touched && registrationForm.controls.email.errors?.['required']">
                    Email is required.
                </p>
            </div>

            <div class="row form-field-row">
                <label for="">Pasword</label>
                <input type="password" formControlName="password">
                <p class="error-message" *ngIf="registrationForm.controls.password.touched && registrationForm.controls.password.errors?.['required']">
                    Password is required.
                </p>
            </div>

            <div class="row form-field-row">
                <button class="signup-button">Sign up</button>
            </div>

            <div class="row form-field-row loggin-option-field">
                <small>Already have an account?
                    <span>Sign in</span>
                </small>
            </div>
        </form>
</div>
