    //     joiningDate: 'June 04, 2023',
    //     roles: ['Admin']
    //   },

    //   {
    //     firstName: "Michael",
    //     lastName: "Scott",
    //     email: "scott@gmail.com",
    //     joiningDate: 'June 04, 2023',
    //     roles: ['Admin']
    //   }

    // );
    this.accounts$ = this.service
      .getAllAccountsAtOrganisation('b4f75fed-37bf-40fc-a8b3-f071b41a3fc1');
  }
}
<div class="container-fluid">
    <div class="row page-header-with-details">

        <div class="col-xs-12 col-sm-12 col-md-9 col-lg-9">
            <div class="row justify-content-between">
                <div class="col">
                    <app-title title="Accounts" />
                    <app-description description="Manage accounts and access" />
                </div>
                <div class="col text-end align-self-end">
                    <div class="">
                        <span class="me-4">13 Accounts</span>
                        <app-button-rect-small actionName="+ Add Member" />
                    </div>
                </div>
            </div>

            <div class="row justify-content-center" *ngIf="(accounts$ | async) as accounts">
                <table>        
                    <tbody>
                        <tr *ngFor="let account of accounts" class="internship-row">
                            <td>
                                <span class="profile-pic profile">MS</span>
                                <span class="profile-name profile ms-2">{{ account.firstName}} {{ account.lastName}}</span>
                                <span class="profile-email profile ms-3">{{ account.email }}</span>
                            </td>
                            <td class="profile-joining-date profile">{{ account.joiningDate | date: 'mediumDate' }}</td>
                            <td></td>
                            <td class="d-flex justify-content-end align-items-center">

                                <app-rect-small actionName="-"/>
                            </td>
                        </tr>
                    </tbody>

                </table>
            </div>
        </div>
    </div>
</div>
